mapper.select:
	
(SELECT review_id, null AS reply_id, review_content, review_reviewer, review_regDate, review_updateDate, review_rating
FROM product_reviews USE INDEX (idx_reviews)
WHERE product_id = #{product_id}
LIMIT #{cri.pageStart}, #{cri.amount})
UNION ALL(
SELECT * FROM (
	SELECT r.review_id, r.reply_id, r.reply_content, r.reply_replier, r.reply_regDate, r.reply_updateDate, null
	FROM review_replies r USE INDEX (idx_replies)
	JOIN (
		SELECT review_id 
		FROM product_reviews USE INDEX (idx_reviews)
		WHERE product_id = #{product_id}
		LIMIT #{cri.pageStart}, #{cri.amount}
            )rr
	ON r.review_id = rr.review_id
) tmp
)ORDER BY review_id DESC

(SELECT review_id, review_reviewer, review_content, review_regDate, review_updateDate, review_rating
FROM product_reviews
WHERE review_reviewer = #{review_reviewer}
LIMIT #{cri.pageStart}, #{cri.amount})
UNION ALL(
SELECT * FROM (
	SELECT r.review_id, r.reply_replier, r.reply_content, r.reply_regDate, r.reply_updateDate, null
	FROM review_replies r USE INDEX (idx_replies)
	JOIN (
		SELECT  review_id 
		FROM product_reviews
		WHERE review_reviewer = #{review_reviewer}
		LIMIT #{cri.pageStart}, #{cri.amount}
            )rr
	ON r.review_id = rr.review_id 
) tmp
)ORDER BY review_id DESC
